        public String printByteBinaryString(byte b){
            StringBuilder sb=new StringBuilder(Integer.toBinaryString(b&255));
            int count=8-sb.length();
            for(int i=0;i<count;++i){
                sb.insert(0,"0");
            }
            return sb.toString();
        }


    /**
     * 编码字符串。
     * @param str 指定的需要编码的字符串
     * @return 编码结果
     */
    public HuffmanEncodeResult encode(String str) {
        ArrayList<Node> letterList = toList(str);
        Node rootNode = createTree(letterList);
        Map<Character, String> letterCode = getLetterCode(rootNode);
        HuffmanEncodeResult result = encode(letterCode, str);
        return result;
    }
    /**
     * 编码字符串。
     * @param letterCode 字符/编码对集合。
     * @param letters 指定的需要编码的字符串。
     * @return 编码结果
     */
    private HuffmanEncodeResult encode(Map<Character, String> letterCode, String letters) {
        StringBuilder encode = new StringBuilder();
        for (int i = 0, length = letters.length(); i < length; i++) {
            Character character = letters.charAt(i);
            encode.append(letterCode.get(character));
        }
        HuffmanEncodeResult result = new HuffmanEncodeResult(encode.toString(), letterCode);
        return result;
    }


    /**
     * 把一个字符串转化为节点列表
     * @param letters
     * @return
     */
    private ArrayList<Node> toList(String letters) {
        ArrayList<Node> letterList = new ArrayList<Node>();
        Map<Character, Integer> ci = new HashMap<Character, Integer>();
        for (int i = 0; i < letters.length(); i++) {
            Character character = letters.charAt(i);
            if (!ci.keySet().contains(character)) {
                ci.put(character, 1);
            } else {
                Integer oldValue = ci.get(character);
                ci.put(character, oldValue + 1);
            }
        }
        Set<Character> keys = ci.keySet();
        for (Character key : keys) {
            Node node = new Node();
            Data data = new Data();
            data.setC(key);
            data.setFrequency(ci.get(key));
            node.setData(data);
            letterList.add(node);
        }
        return letterList;
    }
    /**
     * 获得所有字符编码对
     *
     * @param rootNode 哈夫曼树的根节点
     * @return 所有字符编码对
     */
    private Map<Character, String> getLetterCode(Node rootNode) {
        Map<Character, String> letterCode = new HashMap<Character, String>();
        // 处理只有一个节点的情况
        if (rootNode.getLeftChild() == null && rootNode.getRightChild() == null) {
            letterCode.put(rootNode.getData().getC(), "0");
            return letterCode;

        }
        getLetterCode(rootNode, "", letterCode);
        return letterCode;
    }

    /**
     * 先序遍历哈夫曼树,获得所有字符编码对。
     *
     * @param rooNode 哈夫曼树根结点
     * @param suffix 编码前缀，也就是编码这个字符时，之前路径上的所有编码
     * @param letterCode 用于保存字符编码结果
     */
    private void getLetterCode(Node rooNode, String suffix,
                               Map<Character, String> letterCode) {
        if (rooNode != null) {
            if (rooNode.getLeftChild() == null
                    && rooNode.getRightChild() == null) {//叶子节点
                Character character = rooNode.getData().getC();
                letterCode.put(character, suffix);

            }
            getLetterCode(rooNode.getLeftChild(), suffix + "0", letterCode);
            getLetterCode(rooNode.getRightChild(), suffix + "1", letterCode);

        }
    }



    public String decode(HuffmanEncodeResult decodeResult) {

        String encodedStr=decodeResult.geteEncodedStr();
        byte[] byteArray=null;
        try {
            byteArray=encodedStr.getBytes("ISO-8859-1");
        } catch (UnsupportedEncodingException e) {
            e.printStackTrace();
        }
        byte invalidBitCount=byteArray[byteArray.length-1];
        StringBuilder sb=new StringBuilder();
        for(int i=0;i<byteArray.length-1;++i){
            sb.append(printByteBinaryString(byteArray[i]));
        }
        sb.delete(sb.length()-invalidBitCount,sb.length());
        // 待解码的（被编码的）字符串
        encodedStr=sb.toString();
        // 解码得到的字符串
        StringBuilder decodeStr = new StringBuilder();
        // 获得解码器
        Map<String, Character> decodeMap = getDecoder(decodeResult
                .getLetterCode());
        // 解码器键集合
        Set<String> keys = decodeMap.keySet();

        // 从最短的开始匹配之所以能够成功，是因为哈夫曼编码的唯一前缀性质
        // 临时的可能的键值
        String temp = "";
        // 改变temp值大小的游标
        int index = 0;
        int len=1;
        while(index<sb.length()){
            temp = sb.substring(index, index+len);
            if (keys.contains(temp)) {
                Character character = decodeMap.get(temp);
                decodeStr.append(character);
                index=index+len;
                len=1;
            }else{
                len++;
            }
        }
        return decodeStr.toString();
    }


    /**
     * 获得解码器，也就是通过字母/编码对得到编码/字符对。
     *
     * @param letterCode
     * @return
     */
    private Map<String, Character> getDecoder(Map<Character, String> letterCode) {
        Map<String, Character> decodeMap = new HashMap<String, Character>();
        Set<Character> keys = letterCode.keySet();
        for (Character key : keys) {
            String value = letterCode.get(key);
            decodeMap.put(value, key);
        }
        return decodeMap;
    }

    /*
     * 创建哈夫曼树
     */
    protected Node createTree(ArrayList<Node> letterList) {
        if(letterList.size()<1)
            throw new RuntimeException();
        init(letterList);
        if(letterList.size()==1)
            return letterList.get(0);
        while(true){
            Data data=new Data();
            data.setFrequency(letterList.get(0).getData().getFrequency()+letterList.get(1).getData().getFrequency());
            Node node=new Node();
            node.setData(data);
            node.setLeftChild(letterList.get(0));
            node.setRightChild(letterList.get(1));
            letterList.remove(0);
            letterList.remove(0);
            letterList.add(node);
            if(letterList.size()==1){
                return node;
            }
            Collections.sort(letterList, new Comparator<Node>() {
                @Override
                public int compare(Node o1, Node o2) {
                    return o1.getData().getFrequency()-o2.getData().getFrequency();
                }
            });
        }
    }

    /**
     * 初始化 让节点列表有序
     */
    private void init(ArrayList<Node> letterList) {
        Collections.sort(letterList, new Comparator<Node>() {
            @Override
            public int compare(Node o1, Node o2) {
                return o1.getData().getFrequency()-o2.getData().getFrequency();
            }
        });
    }


